import { ModifierType } from "./ModifierType.js";
import { GestureEventType } from "./GestureEventType.js";
import { Point } from "../../shape/Point.js";
/**
 * GestureEvents are fired when user interaction with the map occurs.  Depending on the
 * {@link GestureEventType}, this event object may contain additional properties.
 * Please refer to  {@link GestureEventType} for details for each event type.
 */
export interface GestureEvent {
  /**
   * The input modality type. Either 'mouse' or 'touch'.
   */
  readonly inputType: string;
  /**
   * The event type. One of the constants from {@link GestureEventType}.
   */
  readonly type: GestureEventType;
  /**
   * The modifier. One of the constants from {@link ModifierType}.
   * A modifier indicates the combination of the modifier keys pressed during the GestureEvent.
   * The modifier keys are Alt, Ctrl and Shift.
   */
  modifier: ModifierType;
  /**
   * The raw dom event generated by the browser.
   */
  readonly domEvent: UIEvent;
  /**
   * A {@link Point} containing the (x,y) position of the event relative to the map (in pixels).
   * 0,0 is the upper left corner of the map.The reference of this point is null.
   *
   * Note that this property contains the same coordinates as {@link viewPosition}.
   * The key difference is that this property is a {@link Point} while {@link viewPosition} is an Array.
   * Because this is a {@link Point}, you can directly pass its values to transformations such as
   * {@link Map.viewToMapTransformation map.viewToMapTransformation}.
   *
   */
  readonly viewPoint: Point;
  /**
   * An array containing the (x,y) position of the event relative to the map. 0,0 is the upper left
   * corner of the map
   */
  readonly viewPosition: number[];
  /**
   * An array containing the (x,y) position of the event in page coordinates. 0,0 is the upper left
   * corner of the currently visible page area in the window. Unlike clientPosition, scrolling offsets are included in
   * this value.
   */
  readonly pagePosition: number[];
  /**
   * An array containing the (x,y) position of the event in client coordinates. 0,0 is the upper left
   * corner of the browser viewport. Scrolling offsets are not included in this value.
   */
  readonly clientPosition: number[];
}